public class EstruturaRepeticao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int contador = 0;
		
		while (contador<=10) {
			System.out.println(contador);
			contador++;
		}
		
		for (int contador2=0; contador2<=10; contador2++) {
			System.out.println(contador2);
		}
	}
	

}
