
public class OperadoresAtribuicao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor;
		
		System.out.println("SOMA >>>>>>>");
		
		valor = 1;
		valor += 1;
		valor ++;
		System.out.println("O valor é: " + valor);
		
		System.out.println("SUBTRACAO >>>>>>");
		
		valor -= 1;
		System.out.println("O valor é: " + valor);
		
		valor --;
		
		
		

	}

}
